import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DishItem from './DishItem';

const DishList = () => {
  const [dishes, setDishes] = useState([]);

  useEffect(() => {
    fetchDishes();
  }, []);

  const fetchDishes = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/dishes/?format=json');
      setDishes(response.data); // Assuming response.data is an array of dishes
    } catch (error) {
      console.error('Error fetching dishes:', error);
    }
  };

//   const togglePublish = async (id) => {
//     try {
//       const response = await axios.post(`http://127.0.0.1:8000/api/dishes/${id}/toggle_publish/`);
//       // Update local state immediately after successful toggle
//       setDishes(dishes.map(dish => dish.id === id ? { ...dish, isPublished: response.data.isPublished } : dish));
//     } catch (error) {
//       console.error('Error toggling publish status:', error);
//     }
//   };

const togglePublish = async (id) => {
    try {
      const response = await axios.post(`http://127.0.0.1:8000/api/dishes/${id}/toggle_publish/`);
      console.log('Toggle publish response:', response.data); // Log response data
      const updatedDishes = dishes.map(dish => {
        if (dish.id === id) {
          return { ...dish, isPublished: response.data.isPublished };
        }
        return dish;
      });
      console.log('Updated dishes:', updatedDishes); // Log updated dishes array
      setDishes(updatedDishes); // Update local state with the updated dishes array
    } catch (error) {
      console.error('Error toggling publish status:', error);
    }
  };
  
  return (
    <div>
      {dishes.map(dish => (
        <DishItem key={dish.id} dish={dish} togglePublish={() => togglePublish(dish.id)} />
      ))}
    </div>
  );
};

export default DishList;
