// DishItem.js

import React from 'react';

const DishItem = ({ dish, togglePublish }) => {
  const dishItemStyle = {
    display: 'inline-block',
    margin: '10px',
    textAlign: 'center',
  };

  const imageStyle = {
    width: '200px',
    height: 'auto',
  };

  return (
    <div style={{ ...dishItemStyle, marginBottom: '20px' }}>
    
      <div className='bg-yellow-100 opacity-5 font-bold text-2xl p-10 rounded-3xl'>
      <img src={dish.imageUrl} alt={dish.dishName} style={imageStyle} />
        <h3 className='mt-5'>{dish.dishName}</h3>
        <p>Published: {dish.isPublished ? 'Yes' : 'No'}</p>
        <button className="bg-gray-500 p-3 mt-10 mb-5 rounded-2xl transition-all duration-500 font-semibold text-black hover:bg-gray-100" onClick={() => togglePublish(dish.id)}>Toggle Publish</button>
      </div>
    </div>
  );
};

export default DishItem;
