// DishItem.js

import React from 'react';

const DishItem = ({ dish, togglePublish }) => {
  return (
    <div style={{ marginBottom: '20px', textAlign: 'center' }}>
      <img src={dish.imageUrl} alt={dish.dishName} style={{ width: '200px', height: 'auto' }} />
      <div>
        <h3>{dish.dishName}</h3>
        <p>Published: {dish.isPublished ? 'Yes' : 'No'}</p>
        <button onClick={() => togglePublish(dish.id)}>Toggle Publish</button>
      </div>
    </div>
  );
};

export default DishItem;
