// DishItem.js

import React from 'react';

const DishItem = ({ dish, togglePublish }) => {
  const dishItemStyle = {
    display: 'inline-block',
    margin: '10px',
    textAlign: 'center',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    backgroundSize: 'cover',  // Ensure the background image covers the entire container
    backgroundImage: `url(./image.jpg)`,  // Relative path to your image
  };

  const imageStyle = {
    width: '200px',
    height: 'auto',
    marginBottom: '10px',  // Adjust spacing between image and text
  };

  return (
    <div style={dishItemStyle}>
      <img src="./image.jpg" alt={dish.dishName} style={imageStyle} /> {/* Relative path for <img> */}
      <div>
        <h3>{dish.dishName}</h3>
        <p>Published: {dish.isPublished ? 'Yes' : 'No'}</p>
        <button onClick={() => togglePublish(dish.id)}>Toggle Publish</button>
      </div>
    </div>
  );
};

export default DishItem;
