import React from 'react';

const DishItem = ({ dish, togglePublish }) => {
  const dishItemStyle = {
    display: 'inline-block',
    margin: '10px',
    textAlign: 'center',
    position: 'relative', // Ensure position relative for overlay positioning
  };

  const imageStyle = {
    width: '200px',
    height: 'auto',
  };

  return (
    <div className="dish-item ml-5" style={dishItemStyle}>
      <div className="overlay">
        
        <div className='bg-gray-400 bg-opacity-50 font-bold text-2xl  p-10 rounded-3xl'>
          <h3 className='mt-5'>{dish.dishName}</h3>
          <p>Published: {dish.isPublished ? 'Yes' : 'No'}</p>
          <button className="bg-gray-500 p-3 mt-10 mb-5 rounded-2xl transition-all duration-500 font-semibold text-black hover:bg-gray-100" onClick={() => togglePublish(dish.id)}>Toggle Publish</button>
        </div>
      </div>
    </div>
  );
};

export default DishItem;
